const RedisTimeMachine = require('./sortedSetManager');

module.exports = class Producer {
	constructor({ url }) {
		this.m_sortedSetProducer = new RedisTimeMachine({ url });
	}

	emit({ json, key, timestamp }) {
		return this.m_sortedSetProducer.emitToSortedSet({ json, key, timestamp });
	}

	setAsExecuted({ executionkey, id, json }) {
		return this.m_sortedSetProducer.setAsExecuted({ executionkey, id, json });
	}
}